,"sync","showFailureToaster",true,"query","WITH yearly_data AS (
  SELECT 
    EXTRACT(YEAR FROM purchase_date) as year,
    SUM(purchase_price::numeric) as total_asset_value,

    -- Book depreciation for the year
    SUM(
      CASE 
        WHEN book_depreciation_method = 'Straight-Line' OR book_depreciation_method IS NULL THEN
          purchase_price::numeric / NULLIF(useful_life_years, 0)
        ELSE 0
      END
    ) as annual_book_depreciation,

    -- Tax depreciation for the year (simplified - first year rates)
    SUM(
      CASE 
        WHEN section_179_election = TRUE 
        THEN COALESCE(NULLIF(section_179_amount, 0), purchase_price::numeric)
        WHEN bonus_depreciation_election = TRUE 
        THEN purchase_price::numeric
        ELSE 
          CASE COALESCE(macrs_class_life, 5)
            WHEN 5 THEN purchase_price::numeric * 0.20
            WHEN 3 THEN purchase_price::numeric * 0.3333
            WHEN 7 THEN purchase_price::numeric * 0.1429
            ELSE purchase_price::numeric * 0.20
          END
      END
    ) as annual_tax_depreciation

  FROM assets 
  WHERE purchase_date IS NOT NULL
  GROUP BY EXTRACT(YEAR FROM purchase_date)
)
SELECT 
  year,
  total_asset_value,
  annual_book_depreciation,
  annual_tax_depreciation
FROM yearly_data
ORDER BY year;","playgroundQueryUuid","","playgroundQueryId",null,"error",null,"workflowRunBodyType","raw","privateParams",["^B",[]],"queryRunOnSelectorUpdate",false,"runWhenPageLoadsDelay","","warningCodes",["^B",[]],"data",null,"recordId","","importedQueryInputs",["^1G",[]],"_additionalScope",["^B",[]],"isImported",false,"showSuccessToaster",false,"dataArray",["^B",[]],"cacheKeyTtl","","filterBy","","requestSentTimestamp",null,"databaseHostOverride","","metadata",null,"editorMode","sql","queryRunTime",null,"actionType","","changesetObject","","shouldUseLegacySql",false,"offlineOptimisticResponse",null,"errorTransformer","return data.error","finished",null,"databaseNameOverride","","confirmationMessage",null,"isFetching",false,"changeset","","rawData",null,"queryTriggerDelay","0","resourceTypeOverride",null,"watchedParams",["^B",[]],"enableErrorTransformer",false,"databaseWarehouseOverride","","enableBulkUpdates",false,"showLatestVersionUpdatedWarning",false,"timestamp",0,"importedQueryDefaults",["^1G",[]],"enableTransformer",false,"showUpdateSetValueDynamicallyToggle",false,"overrideOrgCacheForUserCache",false,"bulkUpdatePrimaryKey","","runWhenPageLoads",false,"transformer","return data","events",["^B",[]],"tableName","","queryTimeout","10000","workflowId",null,"requireConfirmation",false,"queryFailureConditions","","changesetIsObject",false,"enableCaching",false,"allowedGroups",["^B",[]],"databaseUsernameOverride","","databaseRoleOverride","","shouldEnableBatchQuerying",false,"doNotThrowOnNoOp",false,"offlineQueryType","None","queryThrottleTime","750","updateSetValueDynamically",true,"notificationDuration",4.5]],"^1H",null,"^1I",null,"^1J",null,"^1K",null,"^1L",null,"^1M","","^7","~m1750397798302","^1N","~m1750397856295","^1O","","^1P",null,"^1Q",null,"^1R",null,"^1S",null]]],"getCurrentUser",["^0",["^ ","n","pluginTemplate","v",["^ ","id","getCurrentUser","^1@",null,"^1A","datasource","^1B","SqlQueryUnified","^1C",null,"^1D","0685905a-1144-4c3a-943a-7b7b96c9dc95