t_tracker_db","^1F",["^1G",["queryRefreshTime","","allowedGroupIds",["^B",[]],"streamResponse",false,"records","","lastReceivedFromResourceAt",null,"isFunction",false,"databasePasswordOverride","","functionParameters",null,"queryDisabledMessage","","servedFromCache",false,"offlineUserQueryInputs","","functionDescription",null,"successMessage","","queryDisabled","","playgroundQuerySaveId","latest","workflowParams",null,"resourceNameOverride","","runWhenModelUpdates",true,"workflowRunExecutionType","sync","showFailureToaster",true,"query","WITH assets_cte AS (
  SELECT
    a.id                    AS asset_id,
    a.purchase_price        AS cost,
